
public class PartE {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=1; i<5; i ++)
		{
		    for (int k=0; k < (4 - i); k++)
		    {
		        System.out.print(" ");
		    }
		    for (int j=0; j<i; j++)
		    {
		        System.out.print("* ");
		    }
		    System.out.println("");
		}
	}

}
